ConfigurationSchema:
{
    "properties": {
        "DynatraceOAuthAccess": {
            "$ref": "#/definitions/DynatraceOAuthAccess"
        }
    },
    "additionalProperties": false,
    "required": [
        "DynatraceOAuthAccess"
    ],
    "definitions": {
        "DynatraceOAuthAccess": {
            "type": "object",
            "properties": {
                "ClientId": {
                    "description": "oAuth client ID",
                    "type": "string"
                },
                "ClientSecret": {
                    "description": "oAuth client secret",
                    "type": "string"
                },
                "Endpoint": {
                    "description": "Endpoint URL to access Dynatrace API",
                    "type": "string"
                }
            },
            "required": [
                "ClientId",
                "ClientSecret",
                "Endpoint"
            ],
            "additionalProperties": false
        },
        "Tag": {
            "type": "string"
        },
        "Variable": {
            "type": "object",
            "properties": {
                "Name": {
                    "type": "string",
                    "pattern": "^[A-Za-z0-9_]+$",
                    "maxLength": 200,
                    "minLength": 1
                },
                "Definition": {
                    "type": "string",
                    "maxLength": 1000,
                    "minLength": 1
                }
            },
            "required": [
                "Name",
                "Definition"
            ],
            "additionalProperties": false
        },
        "Objective": {
            "type": "object",
            "properties": {
                "Name": {
                    "type": "string",
                    "maxLength": 200,
                    "minLength": 1
                },
                "Description": {
                    "type": "string",
                    "maxLength": 250,
                    "minLength": 1
                },
                "ObjectiveType": {
                    "type": "string",
                    "enum": [
                        "DQL",
                        "REFERENCE_SLO"
                    ]
                },
                "ComparisonOperator": {
                    "type": "string",
                    "enum": [
                        "GREATER_THAN_OR_EQUAL",
                        "LESS_THAN_OR_EQUAL"
                    ]
                },
                "DqlQuery": {
                    "type": "string",
                    "maxLength": 2000,
                    "minLength": 1
                },
                "Target": {
                    "type": "number"
                },
                "Warning": {
                    "type": "number"
                },
                "ReferenceSlo": {
                    "type": "string",
                    "description": "Please enter the metric key of your desired SLO. SLO metric keys have to start with 'func:slo.'",
                    "maxLength": 2000,
                    "minLength": 1
                },
                "AutoAdaptiveThresholdEnabled": {
                    "type": "boolean",
                    "description": "Auto-adaptive thresholds are a dynamic approach to baselining where the reference value for detecting anomalies changes over time. "
                }
            },
            "required": [
                "Name",
                "ObjectiveType",
                "ComparisonOperator"
            ],
            "additionalProperties": false
        }
    },
    "typeName": "Dynatrace::Automation::SiteReliabilityGuardian"
}
