Schema:
{
    "typeName": "NetApp::FSxN::CifsShare",
    "description": "A CIFS share is a shared folder tied to an FSx for ONTAP volume. It allows Windows and other SMB-compatible clients to access files on the volume over the network. CIFS shares can be managed with access controls and permissions to ensure secure and efficient file sharing across users and applications. Once activated, you will need a preview key to consume this resource. Please reach out to Ng-fsx-cloudformation@netapp.com to get the key. To use this resource, you would need to first create the Link module.",
    "sourceUrl": "https://github.com/NetApp/NetApp-CloudFormation-FSx-ONTAP-provider",
    "tagging": {
        "taggable": false
    },
    "definitions": {
        "SecretSource": {
            "type": "object",
            "properties": {
                "SecretArn": {
                    "description": "The ARN of the secret stored in AWS Secrets Manager.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:secretsmanager:(([a-z]+-)+[0-9])?:([0-9]{12})?:secret:[^.]+$"
                },
                "SecretKey": {
                    "description": "Reference for the SecretKey. The actual password is stored in AWS Secret Manager.",
                    "type": "string",
                    "dependencies": {
                        "SecretKey": [
                            "SecretArn"
                        ]
                    }
                }
            },
            "required": [
                "SecretArn",
                "SecretKey"
            ],
            "additionalProperties": false
        },
        "PasswordSource": {
            "type": "object",
            "properties": {
                "Secret": {
                    "description": "A reference to the source of the password, typically an AWS Secrets Manager secret.",
                    "$ref": "#/definitions/SecretSource"
                }
            },
            "required": [
                "Secret"
            ],
            "additionalProperties": false
        },
        "CifsShareAcl": {
            "type": "object",
            "properties": {
                "Permission": {
                    "description": "Access rights that a user or group has on the defined CIFS Share.",
                    "type": "string",
                    "enum": [ "no_access", "read", "change", "full_control" ]
                },
                "Type": {
                    "description": "Type of the user or group to add to the access control list on the defined CIFS share.",
                    "type": "string",
                    "enum": [ "windows", "unix_user", "unix_group" ]
                },
                "UserOrGroup": {
                    "description": "User or group name to add to the access control list on the defined CIFS share.",
                    "type": "string",
                    "pattern": "^[a-zA-Z0-9_-]{1,256}$"
                }
            },
            "required": [
                "Permission",
                "Type",
                "UserOrGroup"
            ],
            "additionalProperties": false
        },
        "SVM": {
            "type": "object",
            "properties": {
                "Name": {
                    "description": "The name of the SVM.",
                    "type": "string"
                },
                "UUID": {
                    "description": "The UUID of the SVM.",
                    "type": "string"
                }
            },
            "oneOf": [
                {
                    "required": [
                        "Name"
                    ]
                },
                {
                    "required": [
                        "UUID"
                    ]
                }
            ],
            "additionalProperties": false
        }
    },
    "properties": {
        "FsxAdminPasswordSource": {
            "description": "The password source for the FSx admin user.",
            "$ref": "#/definitions/PasswordSource"
        },
        "FileSystemId": {
            "description": "The file system ID of the Amazon FSx for NetApp ONTAP file system in which the resource is created.",
            "type": "string",
            "pattern": "^(fs-[0-9a-f]{8,18})$"
        },
        "LinkArn": {
            "description": "The ARN of the AWS Lambda function that will be invoked to manage the resource.",
            "type": "string",
            "pattern": "^arn:aws(-(cn|us-gov))?:lambda:(([a-z]+-)+[0-9])?:([0-9]{12})?:function:[^.]+$"
        },
        "Name": {
            "description": "Name of the CIFS share.",
            "type": "string",
            "pattern": "^[a-zA-Z0-9_-]{1,80}$"
        },
        "SVM": {
            "description": "Existing SVM in which to create the CIFS share.",
            "$ref": "#/definitions/SVM"
        },
        "Path": {
            "description": "Path in the host SVM namespace that is shared through this share.",
            "type": "string",
            "pattern": "^\/[a-zA-Z0-9_-]{1,256}$"
        },
        "Comment": {
            "description": "Text comment about the CIFS share.",
            "type": "string",
            "minLength": 1,
            "maxLength": 256
        },
        "ACLs": {
            "description": "Share permissions that users and groups have on the CIFS share.",
            "type": "array",
            "insertionOrder": false,
            "default": [{ "Permission": "full_control", "Type": "windows", "UserOrGroup": "Everyone" }],
            "items": {
                "$ref": "#/definitions/CifsShareAcl"
            }
        },
        "ShareID": {
            "description": "Uniquely identifies the CIFS share, a concatenation of the SVM name and the CIFS share name.",
            "type": "string"
        }
    },
    "additionalProperties": false,
    "required": [
        "FsxAdminPasswordSource",
        "FileSystemId",
        "LinkArn",
        "Name",
        "SVM",
        "Path"
    ],
    "createOnlyProperties": [
        "/properties/FileSystemId",
        "/properties/FsxAdminPasswordSource",
        "/properties/LinkArn",
        "/properties/Name",
        "/properties/SVM"
    ],
    "readOnlyProperties": [
        "/properties/ShareID"
    ],
    "primaryIdentifier": [
        "/properties/FileSystemId",
        "/properties/Name",
        "/properties/ShareID"
    ],
    "typeConfiguration": {
        "properties": {
            "PreviewKey": {
                "description": "A preview key is a generated key which is required to consume the resource once activated.",
                "type": "string"
            }
        },
        "additionalProperties": false,
        "required": [
            "PreviewKey"
        ]
    },
    "handlers": {
        "create": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "read": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "update": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "delete": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "list": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        }
    }
}

