ConfigurationSchema:
{
    "properties": {
        "PreviewKey": {
            "description": "A preview key is a generated key which is required to consume the resource once activated.",
            "type": "string"
        }
    },
    "additionalProperties": false,
    "required": [
        "PreviewKey"
    ],
    "definitions": {
        "SecretSource": {
            "type": "object",
            "properties": {
                "SecretArn": {
                    "description": "The ARN of the secret stored in AWS Secrets Manager.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:secretsmanager:(([a-z]+-)+[0-9])?:([0-9]{12})?:secret:[^.]+$"
                },
                "SecretKey": {
                    "description": "Reference for the SecretKey. The actual password is stored in AWS Secret Manager.",
                    "type": "string",
                    "dependencies": {
                        "SecretKey": [
                            "SecretArn"
                        ]
                    }
                }
            },
            "required": [
                "SecretArn",
                "SecretKey"
            ],
            "additionalProperties": false
        },
        "PasswordSource": {
            "type": "object",
            "properties": {
                "Secret": {
                    "description": "A reference to the source of the password, typically an AWS Secrets Manager secret.",
                    "$ref": "#/definitions/SecretSource"
                }
            },
            "required": [
                "Secret"
            ],
            "additionalProperties": false
        },
        "FsxnDestination": {
            "type": "object",
            "properties": {
                "FsxAdminPasswordSource": {
                    "description": "The password source for the FSx admin user.",
                    "$ref": "#/definitions/PasswordSource"
                },
                "FileSystemId": {
                    "description": "The file system ID of the Amazon FSx for NetApp ONTAP file system in which the resource is created.",
                    "type": "string",
                    "pattern": "^(fs-[0-9a-f]{8,18})$"
                },
                "LinkArn": {
                    "description": "The ARN of the AWS Lambda function that will be invoked to manage the resource.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:lambda:(([a-z]+-)+[0-9])?:([0-9]{12})?:function:[^.]+$"
                }
            },
            "required": [
                "FsxAdminPasswordSource",
                "FileSystemId",
                "LinkArn"
            ],
            "additionalProperties": false
        },
        "ClusterPeerStatus": {
            "type": "object",
            "properties": {
                "State": {
                    "description": "The current state of the cluster peer relationship. Possible values are 'available', 'partial', 'unavailable', 'pending' and 'unidentified'.",
                    "enum": [
                        "available",
                        "partial",
                        "unavailable",
                        "pending",
                        "unidentified"
                    ],
                    "type": "string"
                },
                "UpdateTime": {
                    "description": "The last time the status of the peer cluster was updated.",
                    "type": "string"
                }
            },
            "additionalProperties": false
        },
        "ClusterPeerInfo": {
            "type": "object",
            "properties": {
                "Name": {
                    "description": "Optional name for the cluster peer relationship. By default, it is the name of the remote cluster.",
                    "type": "string"
                },
                "RemoteIpAddresses": {
                    "description": "Addresses of the remote peers. Assumes existing intercluster LIFs of remote cluster if not provided.",
                    "type": "array",
                    "insertionOrder": false,
                    "items": {
                        "type": "string"
                    },
                    "minItems": 1
                },
                "IpSpace": {
                    "description": "IPspace name of the local intercluster LIFs. Assumes Default IPspace if not provided.",
                    "type": "string",
                    "default": "Default"
                },
                "StatusInfo": {
                    "description": "Status of the cluster peer relationship.",
                    "$ref": "#/definitions/ClusterPeerStatus"
                }
            },
            "additionalProperties": false
        }
    },
    "typeName": "NetApp::FSxN::ClusterPeer"
}
