ConfigurationSchema:
{
    "properties": {
        "PreviewKey": {
            "description": "A preview key is a generated key which is required to consume the resource once activated.",
            "type": "string"
        }
    },
    "additionalProperties": false,
    "required": [
        "PreviewKey"
    ],
    "definitions": {
        "SecretSource": {
            "type": "object",
            "properties": {
                "SecretArn": {
                    "description": "The ARN of the secret stored in AWS Secrets Manager.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:secretsmanager:(([a-z]+-)+[0-9])?:([0-9]{12})?:secret:[^.]+$"
                },
                "SecretKey": {
                    "description": "Reference for the SecretKey. The actual password is stored in AWS Secret Manager.",
                    "type": "string",
                    "dependencies": {
                        "SecretKey": [
                            "SecretArn"
                        ]
                    }
                }
            },
            "required": [
                "SecretArn",
                "SecretKey"
            ],
            "additionalProperties": false
        },
        "PasswordSource": {
            "type": "object",
            "properties": {
                "Secret": {
                    "description": "A reference to the source of the password, typically an AWS Secrets Manager secret.",
                    "$ref": "#/definitions/SecretSource"
                }
            },
            "required": [
                "Secret"
            ],
            "additionalProperties": false
        },
        "SVM": {
            "type": "object",
            "properties": {
                "Name": {
                    "description": "The name of the SVM.",
                    "type": "string"
                },
                "UUID": {
                    "description": "The UUID of the SVM.",
                    "type": "string"
                }
            },
            "oneOf": [
                {
                    "required": [
                        "Name"
                    ]
                },
                {
                    "required": [
                        "UUID"
                    ]
                }
            ],
            "additionalProperties": false
        },
        "Rule": {
            "type": "object",
            "properties": {
                "AllowDeviceCreation": {
                    "description": "Specifies whether or not device creation is allowed.",
                    "type": "boolean",
                    "default": true
                },
                "AllowSuid": {
                    "description": "Specifies whether or not SetUID bits in SETATTR Op is to be honored.",
                    "type": "boolean",
                    "default": true
                },
                "AnonymousUser": {
                    "description": "User ID to which anonymous users are mapped.",
                    "type": "string",
                    "pattern": "^[a-zA-Z0-9_-]+$",
                    "default": "65534"
                },
                "ChownMode": {
                    "description": "Specifies the chown mode, either 'restricted' or 'unrestricted'.",
                    "type": "string",
                    "enum": [
                        "restricted",
                        "unrestricted"
                    ],
                    "default": "restricted"
                },
                "Clients": {
                    "type": "array",
                    "insertionOrder": false,
                    "items": {
                        "type": "object",
                        "properties": {
                            "Match": {
                                "description": "Client IP address or range.",
                                "type": "string",
                                "pattern": "\\b(?:\\d{1,3}\\.){3}\\d{1,3}(?:\\/(?:\\d|[12]\\d|3[0-2]))\\b"
                            }
                        },
                        "required": [
                            "Match"
                        ],
                        "additionalProperties": false
                    }
                },
                "Index": {
                    "description": "The index of the export rule in the export policy.",
                    "type": "integer"
                },
                "NtfsUnixSecurity": {
                    "description": "Specifies the NTFS Unix security options, either 'fail' or 'ignore'.",
                    "type": "string",
                    "enum": [
                        "fail",
                        "ignore"
                    ],
                    "default": "fail"
                },
                "Protocols": {
                    "description": "Access protocol(s) that the export rule describes.",
                    "type": "array",
                    "default": [
                        "any"
                    ],
                    "insertionOrder": false,
                    "items": {
                        "type": "string",
                        "enum": [
                            "any",
                            "nfs",
                            "cifs",
                            "flexcache",
                            "nfs3",
                            "nfs4"
                        ]
                    }
                },
                "RoRule": {
                    "description": "Authentication flavors that the read-only access rule governs.",
                    "type": "array",
                    "insertionOrder": false,
                    "items": {
                        "type": "string",
                        "enum": [
                            "any",
                            "none",
                            "never",
                            "krb5",
                            "krb5i",
                            "krb5p",
                            "ntlm",
                            "sys"
                        ]
                    }
                },
                "RwRule": {
                    "description": "Authentication flavors that the read/write access rule governs.",
                    "type": "array",
                    "insertionOrder": false,
                    "items": {
                        "type": "string",
                        "enum": [
                            "any",
                            "none",
                            "never",
                            "krb5",
                            "krb5i",
                            "krb5p",
                            "ntlm",
                            "sys"
                        ]
                    }
                },
                "Superuser": {
                    "description": "Authentication flavors that the superuser security type governs.",
                    "type": "array",
                    "insertionOrder": false,
                    "items": {
                        "type": "string",
                        "enum": [
                            "any",
                            "none",
                            "never",
                            "krb5",
                            "krb5i",
                            "krb5p",
                            "ntlm",
                            "sys"
                        ]
                    }
                }
            },
            "required": [],
            "additionalProperties": false
        }
    },
    "typeName": "NetApp::FSxN::ExportPolicy"
}
