Schema:
{
    "typeName": "NetApp::FSxN::Lun",
    "description": "A LUN (Logical unit number) is a block storage object within a volume, used to provide storage for applications that require block-level access. A LUN will appear as a virtual disk to the connected host and can be formatted with a file system or used as raw storage, enabling precise control over storage resources and performance for specific workloads. Once activated, you will need a preview key to consume this resource. Please reach out to Ng-fsx-cloudformation@netapp.com to get the key. To use this resource, you would need to first create the Link module.",
    "sourceUrl": "https://github.com/NetApp/NetApp-CloudFormation-FSx-ONTAP-provider",
    "tagging": {
        "taggable": false
    },
    "definitions": {
        "SecretSource": {
            "type": "object",
            "properties": {
                "SecretArn": {
                    "description": "The ARN of the secret stored in AWS Secrets Manager.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:secretsmanager:(([a-z]+-)+[0-9])?:([0-9]{12})?:secret:[^.]+$"
                },
                "SecretKey": {
                    "description": "Reference for the SecretKey. The actual password is stored in AWS Secret Manager.",
                    "type": "string",
                    "dependencies": {
                        "SecretKey": [
                            "SecretArn"
                        ]
                    }
                }
            },
            "required": [
                "SecretArn",
                "SecretKey"
            ],
            "additionalProperties": false
        },
        "PasswordSource": {
            "type": "object",
            "properties": {
                "Secret": {
                    "description": "A reference to the source of the password, typically an AWS Secrets Manager secret.",
                    "$ref": "#/definitions/SecretSource"
                }
            },
            "required": [
                "Secret"
            ],
            "additionalProperties": false
        },
        "IGroupName": {
            "description": "Existing initiator group to map to the specified LUN.",
            "type": "string",
            "pattern": "^[a-zA-Z0-9_-]+$"
        },
        "SVM": {
            "type": "object",
            "properties": {
                "Name": {
                    "description": "The name of the SVM.",
                    "type": "string"
                },
                "UUID": {
                    "description": "The UUID of the SVM.",
                    "type": "string"
                }
            },
            "oneOf": [
                {
                    "required": [
                        "Name"
                    ]
                },
                {
                    "required": [
                        "UUID"
                    ]
                }
            ],
            "additionalProperties": false
        }
    },
    "properties": {
        "FsxAdminPasswordSource": {
            "description": "The password source for the FSx admin user.",
            "$ref": "#/definitions/PasswordSource"
        },
        "FileSystemId": {
            "description": "The file system ID of the Amazon FSx for NetApp ONTAP file system in which the resource is created.",
            "type": "string",
            "pattern": "^(fs-[0-9a-f]{8,18})$"
        },
        "LinkArn": {
            "description": "The ARN of the AWS Lambda function that will be invoked to manage the resource.",
            "type": "string",
            "pattern": "^arn:aws(-(cn|us-gov))?:lambda:(([a-z]+-)+[0-9])?:([0-9]{12})?:function:[^.]+$"
        },
        "UUID": {
            "description": "The UUID of the LUN.",
            "type": "string",
            "pattern": "^[a-z0-9]{8}-([a-z0-9]{4}-){3}[a-z0-9]{12}$"
        },
        "Name": {
            "description": "The fully qualified path name of the LUN composed of a '/vol' prefix, the volume name, and base name of the LUN. Example: /vol/volume1/lun1",
            "type": "string",
            "pattern": "^/vol/[a-zA-Z0-9_-]+/[a-zA-Z0-9_-]+$"
        },
        "QosPolicyName": {
            "description": "The name of the QoS policy for the LUN.",
            "type": "string"
        },
        "Size": {
            "description": "Size of the LUN. Required when creating a non-clone LUN and disallowed when creating a clone of an existing LUN. A clone's size is taken from the source LUN.",
            "type": "integer",
            "maximum": 140737488355328,
            "minimum": 4096
        },
        "IGroups": {
            "description": "The initiator groups mapped to this LUN.",
            "type": "array",
            "insertionOrder": false,
            "items": {
                "$ref": "#/definitions/IGroupName"
            },
            "default": []
        },
        "SVM": {
            "description": "The SVM information associated with the LUN.",
            "$ref": "#/definitions/SVM"
        },
        "OsType": {
            "description": "The host operating system of the initiator group. All initiators in the group should be hosts of the same operating system.",
            "enum": [
                "aix",
                "hpux",
                "hyper_v",
                "linux",
                "netware",
                "openvms",
                "solaris",
                "solaris_efi",
                "vmware",
                "windows",
                "windows_2008",
                "windows_gpt",
                "xen"
            ],
            "type": "string"
        }
    },
    "additionalProperties": false,
    "required": [
        "FsxAdminPasswordSource",
        "FileSystemId",
        "LinkArn",
        "Name",
        "OsType",
        "SVM",
        "Size"
    ],
    "createOnlyProperties": [
        "/properties/FileSystemId",
        "/properties/FsxAdminPasswordSource",
        "/properties/SVM",
        "/properties/OsType"
    ],
    "readOnlyProperties": [
        "/properties/UUID"
    ],
    "primaryIdentifier": [
        "/properties/FileSystemId",
        "/properties/UUID"
    ],
    "typeConfiguration": {
        "properties": {
            "PreviewKey": {
                "description": "A preview key is a generated key which is required to consume the resource once activated.",
                "type": "string"
            }
        },
        "additionalProperties": false,
        "required": [
            "PreviewKey"
        ]
    },
    "handlers": {
        "create": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "read": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "update": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "delete": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "list": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        }
    }
}

