ConfigurationSchema:
{
    "properties": {
        "PreviewKey": {
            "description": "A preview key is a generated key which is required to consume the resource once activated.",
            "type": "string"
        }
    },
    "additionalProperties": false,
    "required": [
        "PreviewKey"
    ],
    "definitions": {
        "SecretSource": {
            "type": "object",
            "properties": {
                "SecretArn": {
                    "description": "The ARN of the secret stored in AWS Secrets Manager.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:secretsmanager:(([a-z]+-)+[0-9])?:([0-9]{12})?:secret:[^.]+$"
                },
                "SecretKey": {
                    "description": "Reference for the SecretKey. The actual password is stored in AWS Secret Manager.",
                    "type": "string",
                    "dependencies": {
                        "SecretKey": [
                            "SecretArn"
                        ]
                    }
                }
            },
            "required": [
                "SecretArn",
                "SecretKey"
            ],
            "additionalProperties": false
        },
        "PasswordSource": {
            "type": "object",
            "properties": {
                "Secret": {
                    "description": "A reference to the source of the password, typically an AWS Secrets Manager secret.",
                    "$ref": "#/definitions/SecretSource"
                }
            },
            "required": [
                "Secret"
            ],
            "additionalProperties": false
        },
        "FsxnDestination": {
            "type": "object",
            "properties": {
                "FsxAdminPasswordSource": {
                    "description": "The password source for the FSx admin user.",
                    "$ref": "#/definitions/PasswordSource"
                },
                "FileSystemId": {
                    "description": "The file system ID of the Amazon FSx for NetApp ONTAP file system in which the resource is created.",
                    "type": "string",
                    "pattern": "^(fs-[0-9a-f]{8,18})$"
                },
                "LinkArn": {
                    "description": "The ARN of the AWS Lambda function that will be invoked to manage the resource.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:lambda:(([a-z]+-)+[0-9])?:([0-9]{12})?:function:[^.]+$"
                }
            },
            "required": [
                "FsxAdminPasswordSource",
                "FileSystemId",
                "LinkArn"
            ],
            "additionalProperties": false
        },
        "NameWithUuidRef": {
            "type": "object",
            "properties": {
                "Name": {
                    "description": "The name part of the reference, which can be used to identify resources such as SVM.",
                    "type": "string"
                },
                "UUID": {
                    "description": "The UUID part of the reference, which can be used to identify resources such as SVM.",
                    "type": "string"
                }
            },
            "oneOf": [
                {
                    "required": [
                        "Name"
                    ]
                },
                {
                    "required": [
                        "UUID"
                    ]
                }
            ],
            "additionalProperties": false
        },
        "Endpoint": {
            "type": "object",
            "properties": {
                "SVM": {
                    "description": "The SVM identifier.",
                    "$ref": "#/definitions/NameWithUuidRef"
                },
                "Volume": {
                    "description": "The volume name within the SVM.",
                    "type": "string"
                }
            },
            "required": [
                "SVM",
                "Volume"
            ],
            "additionalProperties": false
        },
        "SnapMirrorDestinationCreation": {
            "type": "object",
            "properties": {
                "Aggregates": {
                    "description": "List of aggregate names that host the volume.",
                    "type": "array",
                    "uniqueItems": true,
                    "insertionOrder": false,
                    "items": {
                        "type": "string"
                    },
                    "minItems": 1
                }
            },
            "required": [
                "Aggregates"
            ],
            "additionalProperties": false
        },
        "HealthyStatus": {
            "type": "object",
            "properties": {
                "Healthy": {
                    "description": "Indicates whether the relationship is healthy.",
                    "type": "boolean"
                },
                "UnhealthyReason": {
                    "description": "Reason the relationship is not healthy.",
                    "type": "array",
                    "insertionOrder": false,
                    "items": {
                        "type": "string"
                    }
                }
            },
            "additionalProperties": false
        }
    },
    "typeName": "NetApp::FSxN::SnapMirror"
}
