ConfigurationSchema:
{
    "properties": {
        "PreviewKey": {
            "description": "A preview key is a generated key which is required to consume the resource once activated.",
            "type": "string"
        }
    },
    "additionalProperties": false,
    "required": [
        "PreviewKey"
    ],
    "definitions": {
        "SecretSource": {
            "type": "object",
            "properties": {
                "SecretArn": {
                    "description": "The ARN of the secret stored in AWS Secrets Manager.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:secretsmanager:(([a-z]+-)+[0-9])?:([0-9]{12})?:secret:[^.]+$"
                },
                "SecretKey": {
                    "description": "Reference for the SecretKey. The actual password is stored in AWS Secret Manager.",
                    "type": "string",
                    "dependencies": {
                        "SecretKey": [
                            "SecretArn"
                        ]
                    }
                }
            },
            "required": [
                "SecretArn",
                "SecretKey"
            ],
            "additionalProperties": false
        },
        "PasswordSource": {
            "type": "object",
            "properties": {
                "Secret": {
                    "description": "A reference to the source of the password, typically an AWS Secrets Manager secret.",
                    "$ref": "#/definitions/SecretSource"
                }
            },
            "required": [
                "Secret"
            ],
            "additionalProperties": false
        },
        "DateTimeUTC": {
            "description": "Date time format UTC (YYYY-MM-DD hh:mm:ss).",
            "type": "string",
            "pattern": "^((\\d{4})-(0[1-9]|1[0-2])-([0-2]\\d|3[01]) ([01]\\d|2[0-4]):([0-5]\\d):([0-5]\\d))$"
        },
        "NameWithUuidRef": {
            "type": "object",
            "properties": {
                "Name": {
                    "description": "The name part of the reference, which can be used to identify resources such as SVM or volume.",
                    "type": "string"
                },
                "UUID": {
                    "description": "The UUID part of the reference, which can be used to identify resources such as SVM or volume.",
                    "type": "string"
                }
            },
            "oneOf": [
                {
                    "required": [
                        "Name"
                    ]
                },
                {
                    "required": [
                        "UUID"
                    ]
                }
            ],
            "additionalProperties": false
        }
    },
    "typeName": "NetApp::FSxN::Snapshot"
}
