Schema:
{
    "typeName": "NetApp::FSxN::Snapshot",
    "description": "A snapshot is a read-only, point-in-time copy of a volume or a LUN. It preserves the state of the data at the moment the snapshot is created, allowing users to quickly restore files or entire volumes if data is accidentally deleted, corrupted, or changed. Snapshots are highly efficient because they only store changes made after the snapshot was taken, making them ideal for data protection and recovery. Once activated, you will need a preview key to consume this resource. Please reach out to Ng-fsx-cloudformation to get the key. To use this resource, you would need to first create the Link module.",
    "sourceUrl": "https://github.com/NetApp/NetApp-CloudFormation-FSx-ONTAP-provider",
    "tagging": {
        "taggable": false
    },
    "definitions": {
        "SecretSource": {
            "type": "object",
            "properties": {
                "SecretArn": {
                    "description": "The ARN of the secret stored in AWS Secrets Manager.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:secretsmanager:(([a-z]+-)+[0-9])?:([0-9]{12})?:secret:[^.]+$"
                },
                "SecretKey": {
                    "description": "Reference for the SecretKey. The actual password is stored in AWS Secret Manager.",
                    "type": "string",
                    "dependencies": {
                        "SecretKey": [
                            "SecretArn"
                        ]
                    }
                }
            },
            "required": [
                "SecretArn",
                "SecretKey"
            ],
            "additionalProperties": false
        },
        "PasswordSource": {
            "type": "object",
            "properties": {
                "Secret": {
                    "description": "A reference to the source of the password, typically an AWS Secrets Manager secret.",
                    "$ref": "#/definitions/SecretSource"
                }
            },
            "required": [
                "Secret"
            ],
            "additionalProperties": false
        },
        "DateTimeUTC": {
            "description": "Date time format UTC (YYYY-MM-DD hh:mm:ss).",
            "type": "string",
            "pattern": "^((\\d{4})-(0[1-9]|1[0-2])-([0-2]\\d|3[01]) ([01]\\d|2[0-4]):([0-5]\\d):([0-5]\\d))$"
        },
        "NameWithUuidRef": {
            "type": "object",
            "properties": {
                "Name": {
                    "description": "The name part of the reference, which can be used to identify resources such as SVM or volume.",
                    "type": "string"
                },
                "UUID": {
                    "description": "The UUID part of the reference, which can be used to identify resources such as SVM or volume.",
                    "type": "string"
                }
            },
            "oneOf": [
                {
                    "required": ["Name"]
                },
                {
                    "required": ["UUID"]
                }
            ],
            "additionalProperties": false
        }
    },
    "properties": {
        "FsxAdminPasswordSource": {
            "description": "The password source for the FSx admin user.",
            "$ref": "#/definitions/PasswordSource"
        },
        "FileSystemId": {
            "description": "The file system ID of the Amazon FSx for NetApp ONTAP file system in which the resource is created.",
            "type": "string",
            "pattern": "^(fs-[0-9a-f]{8,18})$"
        },
        "LinkArn": {
            "description": "The ARN of the AWS Lambda function that will be invoked to manage the resource.",
            "type": "string",
            "pattern": "^arn:aws(-(cn|us-gov))?:lambda:(([a-z]+-)+[0-9])?:([0-9]{12})?:function:[^.]+$"
        },
        "UUID": {
            "description": "The UUID of the snapshot.",
            "type": "string"
        },
        "Volume": {
            "description": "The reference to the volume associated with the snapshot.",
            "$ref": "#/definitions/NameWithUuidRef"
        },
        "SVM": {
            "description": "The reference to the SVM associated with the snapshot.",
            "$ref": "#/definitions/NameWithUuidRef"
        },
        "Name": {
            "description": "The name of the snapshot copy to be created.",
            "type": "string",
            "maxLength": 255,
            "minLength": 1
        },
        "SnapmirrorLabel": {
            "description": "Label for SnapMirror operations.",
            "type": "string",
            "maxLength": 31,
            "minLength": 1
        },
        "Comment": {
            "description": "Comment associated with the snapshot copy.",
            "type": "string",
            "maxLength": 255,
            "minLength": 1
        },
        "ExpiryTime": {
            "description": "The expiry time for the snapshot copy, matching the date time format: YYYY-MM-DD hh:mm:ss (UTC).",
            "$ref": "#/definitions/DateTimeUTC"
        },
        "SnaplockExpiryTime": {
            "description": "SnapLock expiry time for the snapshot copy, if the snapshot copy is taken on a SnapLock volume. A snapshot copy is not allowed to be deleted or renamed until the SnapLock ComplianceClock time goes beyond this retention time. Will be matching date time format: YYYY-MM-DD hh:mm:ss (UTC).",
            "$ref": "#/definitions/DateTimeUTC"
        }
    },
    "additionalProperties": false,
    "required": [
        "FsxAdminPasswordSource",
        "FileSystemId",
        "LinkArn",
        "Volume",
        "SVM",
        "Name"
    ],
    "createOnlyProperties": [
        "/properties/FileSystemId",
        "/properties/FsxAdminPasswordSource",
        "/properties/Volume",
        "/properties/SVM"
    ],
    "readOnlyProperties": [
        "/properties/UUID"
    ],
    "primaryIdentifier": [
        "/properties/FileSystemId",
        "/properties/UUID"
    ],
    "typeConfiguration": {
        "properties": {
            "PreviewKey": {
                "description": "A preview key is a generated key which is required to consume the resource once activated.",
                "type": "string"
            }
        },
        "additionalProperties": false,
        "required": [
            "PreviewKey"
        ]
    },
    "handlers": {
        "create": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "read": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "update": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "delete": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "list": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        }
    }
}

