ConfigurationSchema:
{
    "properties": {
        "PreviewKey": {
            "description": "A preview key is a generated key which is required to consume the resource once activated.",
            "type": "string"
        }
    },
    "additionalProperties": false,
    "required": [
        "PreviewKey"
    ],
    "definitions": {
        "SecretSource": {
            "type": "object",
            "properties": {
                "SecretArn": {
                    "description": "The ARN of the secret stored in AWS Secrets Manager.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:secretsmanager:(([a-z]+-)+[0-9])?:([0-9]{12})?:secret:[^.]+$"
                },
                "SecretKey": {
                    "description": "Reference for the SecretKey. The actual password is stored in AWS Secret Manager.",
                    "type": "string",
                    "dependencies": {
                        "SecretKey": [
                            "SecretArn"
                        ]
                    }
                }
            },
            "required": [
                "SecretArn",
                "SecretKey"
            ],
            "additionalProperties": false
        },
        "PasswordSource": {
            "type": "object",
            "properties": {
                "Secret": {
                    "description": "A reference to the source of the password, typically an AWS Secrets Manager secret.",
                    "$ref": "#/definitions/SecretSource"
                }
            },
            "required": [
                "Secret"
            ],
            "additionalProperties": false
        },
        "SVM": {
            "type": "object",
            "properties": {
                "Name": {
                    "description": "The name of the SVM.",
                    "type": "string"
                },
                "UUID": {
                    "description": "The UUID of the SVM.",
                    "type": "string"
                }
            },
            "oneOf": [
                {
                    "required": [
                        "Name"
                    ]
                },
                {
                    "required": [
                        "UUID"
                    ]
                }
            ],
            "additionalProperties": false
        },
        "SnapshotPolicyCopy": {
            "type": "object",
            "properties": {
                "Count": {
                    "description": "The number of snapshot copies to maintain for this schedule.",
                    "type": "integer",
                    "minimum": 0,
                    "maximum": 1023
                },
                "ScheduleName": {
                    "description": "Schedule name at which snapshot copies are captured on the volume.",
                    "type": "string"
                },
                "Prefix": {
                    "description": "The prefix to use while creating snapshot copies at regular intervals.",
                    "type": "string"
                },
                "RetentionPeriod": {
                    "description": "The retention period of snapshot copies for this schedule. The retention period value represents a duration and must be specified in the ISO-8601 duration format. The retention period can be in years, months, days, hours, and minutes. A period specified for years, months, and days is represented in the ISO-8601 format as 'PY', 'PM', 'PD' respectively, for example 'P10Y' represents a duration of 10 years. A duration in hours and minutes is represented by 'PTH' and 'PTM' respectively. The period string must contain only a single time element that is, either years, months, days, hours, or minutes. A duration which combines different periods is not supported, for example 'P1Y10M' is not supported.",
                    "type": "string",
                    "pattern": "^(P(\\d+Y|\\d+M|\\d+D|T\\d+H|T\\d+M))$"
                },
                "SnapmirrorLabel": {
                    "description": "Label for SnapMirror operations. SnapMirror Label name must contain at least one character and must not contain leading or trailing white space.",
                    "type": "string"
                }
            },
            "required": [
                "Count",
                "ScheduleName"
            ],
            "additionalProperties": false
        }
    },
    "typeName": "NetApp::FSxN::SnapshotPolicy"
}
