Schema:
{
    "typeName": "NetApp::FSxN::SvmPeer",
    "description": "A storage VM (SVM) peer is a relationship established between two SVMs from different FSX for ONTAP file systems, enabling the sharing of resources and data across file systems. Once activated, you will need a preview key to consume this resource. Please reach out to Ng-fsx-cloudformation@netapp.com to get the key. To use this resource, you would need to first create the Link module.",
    "sourceUrl": "https://github.com/NetApp/NetApp-CloudFormation-FSx-ONTAP-provider",
    "tagging": {
        "taggable": false
    },
    "definitions": {
        "SecretSource": {
            "type": "object",
            "properties": {
                "SecretArn": {
                    "description": "The ARN of the secret stored in AWS Secrets Manager.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:secretsmanager:(([a-z]+-)+[0-9])?:([0-9]{12})?:secret:[^.]+$"
                },
                "SecretKey": {
                    "description": "Reference for the SecretKey. The actual password is stored in AWS Secret Manager.",
                    "type": "string",
                    "dependencies": {
                        "SecretKey": [
                            "SecretArn"
                        ]
                    }
                }
            },
            "required": [
                "SecretArn",
                "SecretKey"
            ],
            "additionalProperties": false
        },
        "PasswordSource": {
            "type": "object",
            "properties": {
                "Secret": {
                    "description": "A reference to the source of the password, typically an AWS Secrets Manager secret.",
                    "$ref": "#/definitions/SecretSource"
                }
            },
            "required": [
                "Secret"
            ],
            "additionalProperties": false
        },
        "Svm": {
            "type": "object",
            "properties": {
                "Name": {
                    "description": "The name of the SVM.",
                    "type": "string"
                },
                "UUID": {
                    "description": "The UUID of the SVM.",
                    "type": "string"
                }
            },
            "oneOf": [
                {
                    "required": ["Name"]
                },
                {
                    "required": ["UUID"]
                }
            ],
            "additionalProperties": false
        },
        "FsxnDestination": {
            "type": "object",
            "properties": {
                "FsxAdminPasswordSource": {
                    "description": "The password source for the FSx admin user.",
                    "$ref": "#/definitions/PasswordSource"
                },
                "FileSystemId": {
                    "description": "The file system ID of the Amazon FSx for NetApp ONTAP file system in which the resource is created.",
                    "type": "string",
                    "pattern": "^(fs-[0-9a-f]{8,18})$"
                },
                "LinkArn": {
                    "description": "The ARN of the AWS Lambda function that will be invoked to manage the resource.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:lambda:(([a-z]+-)+[0-9])?:([0-9]{12})?:function:[^.]+$"
                }
            },
            "required": [
                "FsxAdminPasswordSource",
                "FileSystemId",
                "LinkArn"
            ],
            "additionalProperties": false
        }
    },
    "properties": {
        "FsxAdminPasswordSource": {
            "description": "The password source for the FSx admin user.",
            "$ref": "#/definitions/PasswordSource"
        },
        "FileSystemId": {
            "description": "The file system ID of the Amazon FSx for NetApp ONTAP file system in which the resource is created.",
            "type": "string",
            "pattern": "^(fs-[0-9a-f]{8,18})$"
        },
        "LinkArn": {
            "description": "The ARN of the AWS Lambda function that will be invoked to manage the resource.",
            "type": "string",
            "pattern": "^arn:aws(-(cn|us-gov))?:lambda:(([a-z]+-)+[0-9])?:([0-9]{12})?:function:[^.]+$"
        },
        "FsxnDestinationInfo": {
            "description": "The destination information for the Cluster Peer relationship.",
            "$ref": "#/definitions/FsxnDestination"
        },
        "UUID": {
            "description": "The UUID of the SVM peer relationship.",
            "type": "string"
        },
        "PeerSvmName": {
            "description": "The name of the destination peer SVM.",
            "type": "string"
        },
        "SVM": {
            "description": "The SVM information of the local SVM to peer with the destination SVM.",
            "$ref": "#/definitions/Svm"
        },
        "Applications": {
            "description": "A list of applications that will use the SVM peer relationship.",
            "type": "array",
            "insertionOrder": false,
            "items": {
                "enum": ["snapmirror", "flexcache" ],
                "type": "string"
            }
        }
    },
    "additionalProperties": false,
    "required": [
        "FsxAdminPasswordSource",
        "FileSystemId",
        "LinkArn",
        "FsxnDestinationInfo",
        "PeerSvmName",
        "SVM",
        "Applications"
    ],
    "createOnlyProperties": [
        "/properties/FileSystemId",
        "/properties/FsxAdminPasswordSource",
        "/properties/FsxnDestinationInfo/FileSystemId",
        "/properties/FsxnDestinationInfo/FsxAdminPasswordSource",
        "/properties/PeerSvmName",
        "/properties/SVM"
    ],
    "readOnlyProperties": [
        "/properties/UUID"
    ],
    "primaryIdentifier": [
        "/properties/FileSystemId",
        "/properties/FsxnDestinationInfo/FileSystemId",
        "/properties/UUID"
    ],
    "typeConfiguration": {
        "properties": {
            "PreviewKey": {
                "description": "A preview key is a generated key which is required to consume the resource once activated.",
                "type": "string"
            }
        },
        "additionalProperties": false,
        "required": [
            "PreviewKey"
        ]
    },
    "handlers": {
        "create": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "read": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "update": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "delete": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "list": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        }
    }
}

