{
    "typeName": "NetApp::FSxN::Volume",
    "description": "A volume is a logical storage unit which provides flexible space for data files, snapshots, and block devices. The NetApp:FSxN custom resource allows you to configure and manage FSX for ONTAP volumes by specifying parameters such as volume name, size, storage efficiency, export policies, and other attributes. To use this resource, you must create the Link module. Once activated, you will need a preview key to consume this resource. Please reach out to Ng-fsx-cloudformation@netapp.com to get the key.",
    "sourceUrl": "https://github.com/NetApp/NetApp-CloudFormation-FSx-ONTAP-provider",
    "tagging": {
        "taggable": false
    },
    "definitions": {
        "SecretSource": {
            "type": "object",
            "properties": {
                "SecretArn": {
                    "description": "The ARN of the secret stored in AWS Secrets Manager.",
                    "type": "string",
                    "pattern": "^arn:aws(-(cn|us-gov))?:secretsmanager:(([a-z]+-)+[0-9])?:([0-9]{12})?:secret:[^.]+$"
                },
                "SecretKey": {
                    "description": "Reference for the SecretKey. The actual password is stored in AWS Secret Manager.",
                    "type": "string",
                    "dependencies": {
                        "SecretKey": [
                            "SecretArn"
                        ]
                    }
                }
            },
            "required": [
                "SecretArn",
                "SecretKey"
            ],
            "additionalProperties": false
        },
        "PasswordSource": {
            "type": "object",
            "properties": {
                "Secret": {
                    "description": "A reference to the source of the password, typically an AWS Secrets Manager secret.",
                    "$ref": "#/definitions/SecretSource"
                }
            },
            "required": [
                "Secret"
            ],
            "additionalProperties": false
        },
        "ONTAPTag": {
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "Key": {
                    "description": "The key of the tag.",
                    "type": "string"
                },
                "Value": {
                    "description": "The value of the tag.",
                    "type": "string"
                }
            },
            "required": [
                "Key",
                "Value"
            ]
        },
        "NameWithUuidRef": {
            "type": "object",
            "properties": {
                "Name": {
                    "description": "The name part of the reference, which can be used to identify resources such as SVM, volume or snapshot.",
                    "type": "string"
                },
                "UUID": {
                    "description": "The UUID part of the reference, which can be used to identify resources such as SVM, volume or snapshot.",
                    "type": "string"
                }
            },
            "oneOf": [
                {
                    "required": ["Name"]
                },
                {
                    "required": ["UUID"]
                }
            ],
            "additionalProperties": false
        },
        "RetentionPeriod": {
            "description": "Specifies the allowed retention period for files committed to the WORM state on the volume. The retention value represents a duration and must be specified in the ISO-8601 duration format. The retention period can be in years, months, days, hours, and minutes. A duration specified for years, month,s and days is represented in the ISO-8601 format as 'PY', 'PM', 'PD' respectively, for example 'P10Y' represents a duration of 10 years. A duration in hours and minutes is represented by 'PTH' and 'PTM' respectively. The retention string must contain only a single time element that is, either years, months, days, hours, or minutes. A duration which combines different periods is not supported, for example 'P1Y10M' is not supported. Apart from the duration specified in the ISO-8601 format, the duration field also accepts the string 'infinite' to set an infinite retention period.",
            "type": "string",
            "pattern": "^(P(\\d+Y)?(\\d+M)?(\\d+D)?(T(\\d+H)?(\\d+M)?)?|infinite)$"
        },
        "Efficiency": {
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "Compaction": {
                    "description": "The compaction setting of the volume.",
                    "enum": ["inline", "none", "mixed"],
                    "type": "string"
                },
                "Compression": {
                    "description": "The compression setting of the volume.",
                    "enum": ["inline", "background", "both", "none", "mixed"],
                    "type": "string"
                },
                "Dedupe": {
                    "description": "The deduplication setting of the volume.",
                    "enum": ["inline", "background", "both", "none", "mixed"],
                    "type": "string"
                },
                "CrossVolumeDedupe": {
                    "description": "The cross-volume deduplication setting of the volume.",
                    "enum": ["inline", "background", "both", "none", "mixed"],
                    "type": "string"
                }
            }
        },
        "Snaplock": {
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "SnaplockType": {
                    "description": "The SnapLock type of the volume.",
                    "enum": ["compliance", "enterprise", "non_snaplock"],
                    "type": "string",
                    "default": "non_snaplock"
                },
                "MinimumRetention": {
                    "description": "The minimum retention period for the SnapLock volume.",
                    "$ref": "#/definitions/RetentionPeriod",
                    "default": "P0Y"
                },
                "MaximumRetention": {
                    "description": "The maximum retention period for the SnapLock volume.",
                    "$ref": "#/definitions/RetentionPeriod",
                    "default": "P30Y"
                },
                "DefaultRetention": {
                    "description": "The default retention period for new files in the SnapLock volume.",
                    "$ref": "#/definitions/RetentionPeriod"
                }
            }
        },
        "Tiering": {
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "Policy": {
                    "description": "Policy that determines whether the user data blocks of a volume in a FabricPool will be tiered to the capacity pool storage tier when they become cold.",
                    "enum": ["all", "auto", "none", "snapshot_only"],
                    "type": "string"
                },
                "MinCoolingDays": {
                    "description": "This parameter specifies the minimum number of days that user data blocks of the volume must be cooled before they can be considered cold and tiered out to the capacity pool storage tier.",
                    "type": "integer",
                    "minimum": 2,
                    "maximum": 183
                }
            },
            "required": [
                "Policy"
            ]
        },
        "Clone": {
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "ParentSVM": {
                    "description": "SVM containing the parent volume.",
                    "$ref": "#/definitions/NameWithUuidRef"
                },
                "ParentVolume": {
                    "description": "The parent volume of the clone.",
                    "$ref": "#/definitions/NameWithUuidRef"
                },
                "ParentSnapshot": {
                    "description": "The snapshot of the parent volume from which the clone is created.",
                    "$ref": "#/definitions/NameWithUuidRef"
                },
                "IsCloned": {
                    "description": "Setting 'IsCloned' attribute to false splits the clone from its parent volume.",
                    "type": "boolean",
                    "default": "true"
                }
            },
            "required": [
                "ParentSVM",
                "ParentVolume"
            ]
        },
        "NAS": {
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "SecurityStyle": {
                    "description": "Security style associated with the volume.",
                    "enum": ["mixed", "ntfs", "unix"],
                    "type": "string",
                    "default": "unix"
                },
                "JunctionPath": {
                    "description": "The fully-qualified path in the hosting SVM's namespace at which the volume is mounted.",
                    "type": "string"
                },
                "ExportPolicy": {
                    "description": "The export policy associated with the volume.",
                    "type": "string"
                }
            }
        },
        "Autosize": {
            "type": "object",
            "additionalProperties": false,
            "properties": {
                "Mode": {
                    "description": "The autosize mode of the volume.",
                    "enum": ["grow", "grow_shrink", "off"],
                    "type": "string",
                    "default": "grow"
                },
                "Minimum": {
                    "description": "Minimum size in bytes up to which the volume shrinks automatically. This size cannot be greater than or equal to the maximum size of volume.",
                    "type": "number"
                },
                "Maximum": {
                    "description": "Maximum size in bytes up to which a volume grows automatically. This size cannot be less than the current volume size, or less than or equal to the minimum size of volume.",
                    "type": "number"
                },
                "GrowThreshold": {
                    "description": "The used space threshold percentage for automatic growth of the volume.",
                    "type": "integer"
                },
                "ShrinkThreshold": {
                    "description": "The used space threshold percentage for automatic shrinkage of the volume.",
                    "type": "integer"
                }
            }
        }
    },
    "properties": {
        "FsxAdminPasswordSource": {
            "description": "The password source for the FSx admin user.",
            "$ref": "#/definitions/PasswordSource"
        },
        "FileSystemId": {
            "description": "The file system ID of the Amazon FSx for NetApp ONTAP file system in which the resource is created.",
            "type": "string",
            "pattern": "^(fs-[0-9a-f]{8,18})$"
        },
        "LinkArn": {
            "description": "The ARN of the AWS Lambda function that will be invoked to manage the resource.",
            "type": "string",
            "pattern": "^arn:aws(-(cn|us-gov))?:lambda:(([a-z]+-)+[0-9])?:([0-9]{12})?:function:[^.]+$"
        },
        "UUID": {
            "description": "The UUID of the volume.",
            "type": "string"
        },
        "SVM": {
            "description": "The SVM that contains the volume.",
            "$ref": "#/definitions/NameWithUuidRef"
        },
        "Name": {
            "description": "Volume name. The name of volume must start with an alphabetic character (a to z or A to Z) or an underscore (_). The name must be 197 or fewer characters in length for FlexGroups, and 203 or fewer characters in length for all other types of volumes. Volume names must be unique within an SVM.",
            "type": "string",
            "pattern": "^[a-zA-Z_][a-zA-Z0-9_]{0,202}$"
        },
        "Size": {
            "description": "Physical size of the volume, in bytes. The minimum size for a FlexVol volume is 20MB and the minimum size for a FlexGroup volume is 200MB per constituent. The recommended size for a FlexGroup volume is a minimum of 100GB per constituent. For all volumes, the default size is equal to the minimum size.",
            "type": "number",
            "minimum": 20971520
        },
        "ONTAPTags": {
            "description": "Tags associated with the ONTAP volume.",
            "type": "array",
            "maxItems": 64,
            "uniqueItems": true,
            "insertionOrder": false,
            "items": {
                "$ref": "#/definitions/ONTAPTag"
            }
        },
        "Aggregates": {
            "description": "List of aggregate names that host the volume.",
            "type": "array",
            "uniqueItems": true,
            "insertionOrder": false,
            "items": {
                "type": "string"
            },
            "minItems": 1
        },
        "ConstituentsPerAggregate": {
            "description": "Specifies the number of times to iterate constituents over the aggregates when creating or expanding a FlexGroup volume.",
            "type": "integer",
            "minimum": 1,
            "maximum": 200,
            "default": 20
        },
        "SnapshotPolicy": {
            "description": "Snapshot policy name.",
            "type": "string",
            "default": "default"
        },
        "Encryption": {
            "description": "Indicates if the volume is encrypted.",
            "type": "boolean",
            "default": "false"
        },
        "Type": {
            "description": "The type of volume (e.g., read-write or data protection).",
            "enum": ["rw", "dp"],
            "type": "string",
            "default": "rw"
        },
        "Style": {
            "description": "The style of the volume (e.g., FlexVol or FlexGroup).",
            "enum": ["flexvol", "flexgroup"],
            "type": "string",
            "default": "flexvol"
        },
        "State": {
            "description": "The state of the volume (e.g., online, offline, restricted). Client access is supported only when volume is online and connected to its junction path. Taking volume to offline or restricted state removes its junction path and blocks client access.",
            "enum": ["offline", "online", "restricted"],
            "type": "string",
            "default": "online"
        },
        "AntiRansomwareState": {
            "description": "The anti-ransomware state of the volume.",
            "enum": ["disabled", "dry_run", "enabled", "paused"],
            "type": "string"
        },
        "Efficiency": {
            "description": "The storage efficiency settings for the volume.",
            "$ref": "#/definitions/Efficiency"
        },
        "Snaplock": {
            "description": "The SnapLock settings of the volume.",
            "$ref": "#/definitions/Snaplock"
        },
        "Tiering": {
            "description": "The tiering settings of the volume.",
            "$ref": "#/definitions/Tiering"
        },
        "Clone": {
            "description": "The clone settings of the volume.",
            "$ref": "#/definitions/Clone"
        },
        "NAS": {
            "description": "The NAS settings of the volume.",
            "$ref": "#/definitions/NAS"
        },
        "Autosize": {
            "description": "The autosize settings of the volume.",
            "$ref": "#/definitions/Autosize"
        }
    },
    "additionalProperties": false,
    "required": [
        "FsxAdminPasswordSource",
        "FileSystemId",
        "LinkArn",
        "SVM",
        "Name"
    ],
    "readOnlyProperties": [
        "/properties/UUID"
    ],
    "createOnlyProperties": [
        "/properties/FileSystemId",
        "/properties/FsxAdminPasswordSource",
        "/properties/SVM",
        "/properties/Aggregates",
        "/properties/Type",
        "/properties/Style",
        "/properties/Encryption",
        "/properties/ConstituentsPerAggregate",
        "/properties/Clone/ParentSVM",
        "/properties/Clone/ParentVolume",
        "/properties/Clone/ParentSnapshot",
        "/properties/Snaplock/SnaplockType"
    ],
    "writeOnlyProperties": [
        "/properties/Autosize",
        "/properties/Efficiency",
        "/properties/ConstituentsPerAggregate"
    ],
    "primaryIdentifier": [
        "/properties/FileSystemId",
        "/properties/UUID"
    ],
    "typeConfiguration": {
        "properties": {
            "PreviewKey": {
                "description": "A preview key is a generated key which is required to consume the resource once activated.",
                "type": "string"
            }
        },
        "additionalProperties": false,
        "required": [
            "PreviewKey"
        ]
    },
    "handlers": {
        "create": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "read": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "update": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "delete": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        },
        "list": {
            "permissions": [
                "fsx:DescribeFileSystems",
                "lambda:InvokeFunction",
                "secretsmanager:GetSecretValue"
            ]
        }
    }
}

