Schema:
{
    "typeName": "PaloAltoNetworks::CloudNGFW::NGFW",
    "description": "A Firewall resource offers Palo Alto Networks next-generation firewall capabilities with built-in resiliency, scalability, and life-cycle management.",
    "sourceUrl": "https://github.com/aws-cloudformation/aws-cloudformation-rpdk.git",
    "definitions" : {
        "LogProfileConfig": {
            "title": "LogProfileConfig",
            "description": "Add Log profile config",
            "type": "object",
            "properties": {
                "LogDestination": {
                    "title": "Logdestination",
                    "minLength": 1,
                    "maxLength": 128,
                    "type": "string"
                },
                "LogDestinationType": {
                    "title": "Logdestinationtype",
                    "enum": ["S3", "CloudWatchLogs", "KinesisDataFirehose"],
                    "type": "string"
                },
                "LogType": {
                    "title": "Logtype",
                    "enum": ["TRAFFIC", "DECRYPTION", "THREAT"],
                    "type": "string"
                }
            },
            "required": ["LogDestination", "LogDestinationType", "LogType"],
            "additionalProperties": false
        },
        "SubnetMappings": {
            "type": "array",
            "items": {
                "type": "object",
                "properties": {
                    "AvailabilityZone": {
                        "title": "availabilityZone",
                        "type": "string"
                    },
                    "SubnetId": {
                        "title": "subnetId",
                        "type": "string"
                    }
                },
                "additionalProperties": false
            }
        },
        "AccountId": {
            "title": "Accountid",
            "pattern": "^[0-9]+$",
            "type": "string",
            "minLength": 1
        },
        "AppIdVersion": {
            "title": "Appidversion",
            "minLength": 1,
            "maxLength": 64,
            "pattern": "^[0-9]+-[0-9]+$",
            "type": "string"
        },
        "AutomaticUpgradeAppIdVersion": {
            "title": "Automaticupgradeappidversion",
            "default": true,
            "type": "boolean"
        },
        "EndpointMode": {
            "title": "Endpointmode: CustomerManaged Or ServiceManaged",
            "enum": ["ServiceManaged", "CustomerManaged"],
            "type": "string"
        },
        "FirewallName": {
            "title": "Firewallname",
            "minLength": 1,
            "maxLength": 128,
            "pattern": "^[a-zA-Z0-9-]+$",
            "type": "string"
        },
        "RuleStackName": {
            "title": "Rulestackname",
            "type": "string",
            "minLength": 1
        },
        "GlobalRuleStackName": {
            "title": "GlobalRuleStackName",
            "type": "string",
            "minLength": 1
        },
        "VpcId": {
            "title": "Vpcid",
            "type": "string",
            "minLength": 1
        },
        "LinkId": {
            "title": "LinkId",
            "type": "string",
            "minLength": 1
        },
        "FirewallStatus": {
            "title": "firewallstatus",
            "type": "string"
        },
        "RuleStackStatus": {
            "title": "rulestackstatus",
            "type": "string"
        },
        "FailureReason": {
            "title": "failurereason",
            "type": "string"
        },
        "Attachment": {
            "title": "attachment",
            "type": "object",
            "properties": {
                "EndpointId": {
                    "title": "endpointid",
                    "type": "string"
                },
                "Status": {
                    "title": "status",
                    "type": "string"
                },
                "RejectedReason": {
                    "title": "rejectedreason",
                    "type": "string"
                },
                "SubnetId": {
                    "title": "subnetid",
                    "type": "string"
                },
                "AccountId": {
                    "title": "accountid",
                    "type": "string"
                },
                "VpcId": {
                    "title": "vpcid",
                    "type": "string"
                }
            },
            "additionalProperties": false
        },
        "Tag": {
            "title": "Tag",
            "type": "object",
            "properties": {
                "Key": {
                    "title": "Key",
                    "minLength": 1,
                    "maxLength": 128,
                    "type": "string"
                },
                "Value": {
                    "title": "Value",
                    "minLength": 1,
                    "maxLength": 128,
                    "type": "string"
                }
            },
            "required": ["Key", "Value"],
            "additionalProperties": false
        }
    },
    "properties": {
        "AccountId": {
            "$ref": "#/definitions/AccountId"
        },
        "AppIdVersion": {
            "$ref": "#/definitions/AppIdVersion"
        },
        "AutomaticUpgradeAppIdVersion": {
            "$ref": "#/definitions/AutomaticUpgradeAppIdVersion"
        },
        "Description": {
            "title": "Description",
            "type": "string",
            "minLength": 1
        },
        "EndpointMode": {
            "$ref": "#/definitions/EndpointMode"
        },
        "FirewallName": {
            "$ref": "#/definitions/FirewallName"
        },
        "MultiVpcEnable": {
            "title": "MultiVpcEnable",
            "type": "boolean"
        },
        "Describe": {
            "title": "describe",
            "type": "boolean",
            "default": false
        },
        "RuleStackName": {
            "$ref": "#/definitions/RuleStackName"
        },
        "GlobalRuleStackName": {
            "$ref": "#/definitions/GlobalRuleStackName"
        },
        "SubnetMappings": {
            "$ref": "#/definitions/SubnetMappings"
        },
        "AssociateSubnetMappings": {
            "$ref": "#/definitions/SubnetMappings"
        },
        "DisassociateSubnetMappings": {
            "$ref": "#/definitions/SubnetMappings"  
        },
        "VpcId": {
            "$ref": "#/definitions/VpcId"
        },
        "LinkId": {
            "$ref": "#/definitions/LinkId"
        },
        "LogDestinationConfigs": {
            "title": "Logdestinationconfigs",
            "type": "array",
            "items": {
                "$ref": "#/definitions/LogProfileConfig"
            }
        },
        "CloudWatchMetricNamespace": {
            "title": "Cloudwatchmetricnamespace",
            "type": "string",
            "minLength": 1
        },
        "Tags": {
            "title": "Tags",
            "maxItems": 200,
            "type": "array",
            "items": {
                "$ref": "#/definitions/Tag"
            }
        },
        "VPCIds": {
            "title": "vpcids",
            "maxItems": 200,
            "type": "array",
            "items": {
                "$ref": "#/definitions/VpcId"
            }
        },
        "ReadFirewall": {
            "title": "readfirewall",
            "type": "object",
            "properties": {
                "AccountId": {
                    "$ref": "#/definitions/AccountId"
                },
                "AppIdVersion": {
                    "$ref": "#/definitions/AppIdVersion"
                },
                "AutomaticUpgradeAppIdVersion": {
                    "$ref": "#/definitions/AutomaticUpgradeAppIdVersion"
                },
                "EndpointMode": {
                    "$ref": "#/definitions/EndpointMode"
                },
                "Description": {
                    "title": "Description",
                    "type": "string",
                    "minLength": 1
                },
                "FirewallName": {
                    "$ref": "#/definitions/FirewallName"
                },
                "MultiVpcEnable": {
                    "title": "MultiVpcEnable",
                    "type": "boolean"
                },
                "RuleStackName": {
                    "$ref": "#/definitions/RuleStackName"
                },
                "GlobalRuleStackName": {
                    "$ref": "#/definitions/GlobalRuleStackName"
                },
                "SubnetMappings": {
                    "$ref": "#/definitions/SubnetMappings"
                },
                "VpcId": {
                    "$ref": "#/definitions/VpcId"
                },
                "LinkId": {
                    "$ref": "#/definitions/LinkId"
                },
                "LinkStatus": {
                    "title": "linkstatus",
                    "type": "string"
                },
                "EndpointServiceName": {
                    "title": "endpointservicename",
                    "type": "string"
                },
                "Attachments": {
                    "title": "attachments",
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/Attachment"
                    }
                },
                "FirewallStatus": {
                    "$ref": "#/definitions/FirewallStatus"
                },
                "RuleStackStatus": {
                    "$ref": "#/definitions/RuleStackStatus"
                },
                "FailureReason": {
                    "$ref": "#/definitions/FailureReason"
                },
                "Tags": {
                    "title": "Tags",
                    "maxItems": 200,
                    "type": "array",
                    "items": {
                        "$ref": "#/definitions/Tag"
                    }
                }
            },
            "additionalProperties": false
        }
    },
    "additionalProperties": false,
    "required": [
        "FirewallName",
        "AccountId"
    ],
    "createOnlyProperties": [
        "/properties/FirewallName",
        "/properties/AccountId"
    ],
    "readOnlyProperties": [
        "/properties/ReadFirewall",
        "/properties/ReadFirewall/AccountId",
        "/properties/ReadFirewall/AppIdVersion",
        "/properties/ReadFirewall/AutomaticUpgradeAppIdVersion",
        "/properties/ReadFirewall/EndpointMode",
        "/properties/ReadFirewall/FirewallName",
        "/properties/ReadFirewall/MultiVpcEnable",
        "/properties/ReadFirewall/Description",
        "/properties/ReadFirewall/VpcId",
        "/properties/ReadFirewall/SubnetMappings",
        "/properties/ReadFirewall/LinkId",
        "/properties/ReadFirewall/Attachments",
        "/properties/ReadFirewall/LinkStatus",
        "/properties/ReadFirewall/FirewallStatus",
        "/properties/ReadFirewall/RuleStackStatus",
        "/properties/ReadFirewall/FailureReason",
        "/properties/ReadFirewall/EndpointServiceName",
        "/properties/ReadFirewall/Tags",
        "/properties/ReadFirewall/RuleStackName",
        "/properties/ReadFirewall/GlobalRuleStackName"
    ],
    "primaryIdentifier": [
        "/properties/FirewallName",
        "/properties/AccountId"
    ],
    "handlers": {
        "create": {
            "permissions": [
                "execute-api:Invoke"
            ]
        },
        "read": {
            "permissions": [
                "execute-api:Invoke"
            ]
        },
        "update": {
            "permissions": [
                "execute-api:Invoke"
            ]
        },
        "delete": {
            "permissions": [
                "execute-api:Invoke"
            ]
        },
        "list": {
            "permissions": [
                "execute-api:Invoke"
            ]
        }
    }
}
